    .site-header .mobile-menu-trigger span:after,.site-header .mobile-menu-trigger span:before {
        content: "";
        position: absolute;
        left: 0;
        width: 100%;
        height: 100%;
        background-color: #13111a
    }

    .reveal-header .mobile-menu-trigger span:after,.reveal-header .mobile-menu-trigger span:before {
        background-color: #13111a!important
    }

    .dark-mode-texts .mobile-menu-trigger span,.dark-mode-texts .mobile-menu-trigger span:after,.dark-mode-texts .mobile-menu-trigger span:before {
        background-color: #13111a
    }

    .site-header .mobile-menu-trigger span:before {
        top: -8px
    }

    .site-header .mobile-menu-trigger span:after {
        top: 8px
    }

    .site-header .item-right {
        align-items: center
    }

    .site-header .menu-block {
        position: fixed;
        width: 320px;
        background-color: #fff;
        left: 0;
        top: 0;
        height: 100%;
        overflow: hidden;
        transform: translate(-100%);
        transition: all .5s ease;
        z-index: 1099
    }

    .site-header .menu-block.active {
        transform: translate(0)
    }

    .site-menu-main>li {
        line-height: 1;
        margin: 0;
        display: block
    }

    .site-menu-main>li>a {
        line-height: 50px;
        height: 50px;
        padding: 0 50px 0 15px;
        display: block;
        border-bottom: 1px solid rgba(0,0,0,.1)
    }

    .site-menu-main>li>a i {
        position: absolute;
        height: 50px;
        width: 50px;
        top: 0;
        right: 0;
        text-align: center;
        line-height: 50px;
        transform: rotate(-90deg)
    }

    .site-header .menu-block .mobile-menu-head {
        display: flex;
        height: 50px;
        border-bottom: 1px solid rgba(0,0,0,.1);
        justify-content: space-between;
        align-items: center;
        position: relative;
        z-index: 501;
        position: -webkit-sticky;
        position: sticky;
        background-color: #fff;
        top: 0
    }

    .site-header .menu-block .mobile-menu-head .go-back {
        height: 50px;
        width: 50px;
        border-right: 1px solid rgba(0,0,0,.1);
        cursor: pointer;
        line-height: 50px;
        text-align: center;
        color: #000;
        font-size: 16px;
        display: none
    }

    .site-header .menu-block .mobile-menu-head.active .go-back {
        display: block
    }

    .site-header .menu-block .mobile-menu-head .current-menu-title {
        font-size: 15px;
        font-weight: 500;
        color: #000;
        visibility: hidden;
        text-transform: capitalize
    }

    .site-header .menu-block .mobile-menu-head.active .current-menu-title {
        visibility: visible
    }

    .site-header .menu-block .mobile-menu-head .mobile-menu-close {
        height: 50px;
        width: 50px;
        border-left: 1px solid rgba(0,0,0,.1);
        cursor: pointer;
        line-height: 50px;
        text-align: center;
        color: #000;
        font-size: 25px
    }

    .site-header .menu-block .site-menu-main {
        height: 100%;
        overflow-x: hidden;
        overflow-y: auto
    }

    .site-menu-main>li .sub-menu,.site-menu-main>li .sub-menu.mega-menu {
        visibility: hidden;
        opacity: 0;
        position: relative;
        box-shadow: none;
        margin: 0;
        top: 0;
        left: 0;
        width: 100%;
        height: 0;
        max-width: none;
        min-width: auto;
        overflow-y: auto;
        overflow-x: hidden;
        padding: 0;
        transition: all .5s linear;
        transform: none
    }

    .site-menu-main>li .sub-menu.active {
        padding: 0 0 15px 20px;
        visibility: visible;
        opacity: 1;
        height: 100%
    }

    .site-menu-main>li .sub-menu.mega-menu>.list-item>ul>li>a,.site-menu-main>li .sub-menu>ul>li>a {
        display: block
    }

    .site-menu-main>li .sub-menu.mega-menu>.list-item>ul {
        margin-bottom: 15px
    }

    .menu-overlay {
        position: fixed;
        background-color: rgba(0,0,0,.5);
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        z-index: 1098;
        visibility: hidden;
        opacity: 0;
        transition: all .5s ease
    }

    .menu-overlay.active {
        visibility: visible;
        opacity: 1
    }
}

