}

.wrapper{
  width: 100%;
  padding: 0;
  white-space: nowrap;
  overflow: hidden;
  position: relative;
  z-index: 0;
}

.card{
  float: left;
  width: 20%;
  background: transparent;
  border: none;
  text-align: center;
  position: relative;
}

.card-content{
  margin: 5px;
  color: #fff;
  font-size: 12px;
  border: none;
  overflow: hidden;
  position: relative;
}

.percent{
  height: 27px;
  display: flex;
  overflow: hidden;
  line-height: 0;
  font-size: .75rem;
  background-color: #e9ecef;
  /* border-radius: .25rem; */
  position: relative;
  z-index: 1;
  border-radius: 18px;
  width: 90%;
  margin: 0 auto;
}

.percent p{
  z-index: 15;    
  position: absolute;
  text-align: center;
  width: 100%;
  font-size: 14px;
  font-weight: bold;
  transform: translateY(14px);
  color: black;
}

.percent-bar{
  /* background-color: #ffc107; */
  background-image: linear-gradient(45deg,rgba(255,255,255,.15) 25%,transparent 25%,transparent 50%,rgba(255,255,255,.15) 50%,rgba(255,255,255,.15) 75%,transparent 75%,transparent);
  background-size: 1rem 1rem;
  display: -ms-flexbox;
  display: flex;
  -ms-flex-direction: column;
  flex-direction: column;
  -ms-flex-pack: center;
  justify-content: center;
  overflow: hidden;
  color: #fff;
  text-align: center;
  white-space: nowrap;
  /* background-color: #007bff; */
  transition: width .6s ease;
  -webkit-animation: progress-bar-stripes 1s linear infinite;
  animation: progress-bar-stripes 1s linear infinite;
  /* animation: ; */
  z-index: 10;
}

.yellow{
